import "./style.css"
import React, { useState, useRef } from "react"

export default function Contact() {

    const [senderName, setName] = useState("")
    const [senderEmail, setEmail] = useState("")
    const [senderMessage, setMessage] = useState("")


    function handleChange(e) {
        // e.preventDefault()
        const stateName = e.target.id
        const stateValue = e.target.value

        if (stateName === senderName) {
            setName(stateValue)
        }
        else if (stateName === senderEmail) {
            setEmail(stateValue)
        }
        else if (stateName === senderMessage) {
            setMessage(stateValue)
        }

    }

    return (
        <>
            <div className="container">
                <p id="contactMe" >SEND A MESSAGE</p>
                <div id="line" ></div>
                <div className="row">
                    <div className="mb-3">
                        <label for="senderName" className="form-label">Full Name</label>
                        <input type="text" className="form-control" id="senderName" onChange={handleChange()} placeholder="Enter your full name" />
                    </div>
                    <div className="mb-3">
                        <label for="senderEmail" className="form-label">Email address</label>
                        <input type="email" className="form-control" id="senderEmail" onChange={handleChange()} placeholder="Enter your email address" />
                    </div>
                    <div className="mb-3">
                        <label for="senderMessage" className="form-label">Message</label>
                        <textarea className="form-control" id="senderMessage" rows="6" onChange={handleChange()} placeholder="Enter your message here"></textarea>
                    </div>
                    <div className="mb-3" style={{ textAlign: "center" }}>
                        <button type="button" class="btn" id="btnSend">Primary</button>
                    </div>
                    <div>
                        <p>Name: {senderName}, Email: {senderEmail}, Message: {senderMessage}</p>
                    </div>
                </div>

            </div>

        </>
    )
}
